import React from 'react';
import Subrules from './Subrules';
import isEmpty from "is-empty";

class Rules extends React.Component {
    constructor(props) {
        super(props);

        this.generateRules = this.generateRules.bind(this);
    }

    checkSubRule = (rule) =>{
        if (rule.tag && !isEmpty(rule.params.list)) {
            if(rule.params.list[0].name){
                return (
                    <Rules
                        rules={rule.params.list}
                        callBackendNow={this.props.callBackendNow}
                        selectedModules={this.props.selectedModules}
                        updateCallBackendNow={this.props.updateCallBackendNow}/>
                    )
            }
        }
    }

    generateRules() {
        const rule = this.props.rules;
        // if(!Array.isArray(rule)) {
        //     rule = [rule];   
        // } 
        console.log(rule)
        return (
            <div>
                {   
                    rule.map(rule => (
                                        <ul>
                                            <Subrules
                                                ruleName={rule.name}
                                                ruleTag={rule.tag}
                                                ruleDesc= {rule.desc}
                                                callBackendNow={this.props.callBackendNow}
                                                selectedModules={this.props.selectedModules}
                                                updateCallBackendNow={this.props.updateCallBackendNow}/>

                                            {
                                            rule.tag && !isEmpty(rule.params.list) && rule.params.list[0].name &&
                                                (<Rules
                                                    rules={rule.params.list}
                                                    callBackendNow={this.props.callBackendNow}
                                                    selectedModules={this.props.selectedModules}
                                                    updateCallBackendNow={this.props.updateCallBackendNow}/>)}
                                        </ul>
                                    )
                            )
                }
            </div>
        )
    }

    render() {
        return (
            <div>
                {this.generateRules()}
            </div>
        )
    }
}

export default Rules;
