import React, { useState } from "react";
import SideNav from "./SideNav";
import { Card } from "react-bootstrap";
import { connect } from "react-redux";
import PropTypes from "prop-types";

const AccountSettings = (props) => {
    const [isEditing, setIsEditing] = useState(false);

    return (
        <div className="settings">
            <SideNav active="account"/>
                <Card className="container" id="degree-settings">
                    <Card.Header>
                        Account Settings
                    </Card.Header>
                    <table className="table table-hover settings-table" id="degree-acad-table">
                        <tbody>
                            <tr>
                                <td>
                                    <label>Username :</label>
                                </td>

                                <td>
                                    {props.auth.user.name}
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label>Email :</label>
                                </td>

                                <td>
                                    {props.auth.user.email}
                                </td>
                            </tr>
                        </tbody>
                    </table>

                    <button 
                        className="button settings-button" id="all-settings"
                        onClick={() => props.setIsEditing(true)}>
                            Edit Settings
                    </button>

                </Card>
        </div>
    )
}

AccountSettings.propType = {
    auth: PropTypes.object.required
};

const mapStateToProp = state => ({
    auth: state.auth
});

export default connect(mapStateToProp) (AccountSettings);
