import React from "react";

export const Table = (props) => {

    let totalMCs = 0;

    const generateTable = () => {
        if(semester === "Overview") {

        }
        return props.modplan.selectedModules
            .filter((object) => object.location === semester)
            .map((object) => {
                const { moduleCode, title, moduleCredit } = object;
                return (
                    <tr key={moduleCode}>
                        <td>{title}</td>
                        <td>{moduleCode}</td>
                        <td>{moduleCredit}</td>
                        <td>
                            <Select 
                                name={object}
                                value={object.grade || object.targetGrade} 
                                onChange={handleGradeClick}
                                style={{ width: 70 }}>
                                {/* <Option selected disabled>Grade</Option> */}
                                {generateOptions(gradeList, "grade", object)}
                            </Select>
                        </td>
                        {isPast && 
                        <td>
                            <input
                                className="checkbox"
                                type="checkbox"
                                checked={object.SU}
                                onChange={(e) => handleCheckboxChange(e, object)}/>
                        </td>}
                        <td>
                            <i  
                                className="fa fa-trash-alt fa-border"
                                style={{cursor: "pointer"}}
                                onClick={() => props.setModuleLocation({id: object.moduleCode}, null, null, props.modplan.selectedModules)} />
                        </td>
                    </tr>
                )
        })   
    }

    const makeTable = () => {
        return (
            props.module.filter((object, i) => object.location === props.title)
            .map((object, i) => {
                    const {moduleCode, title, moduleCredit} = object;
                    const render = (
                        <div>
                        <td>{moduleCode}</td>
                        <td>{title}</td>
                        <td>{moduleCredit + 'MCs'}</td>
                        </div>);

                    if(props.category === "tables") {
                        const desc = title.replace(/ /g, '-');
                        const link = `https://nusmods.com/modules/${moduleCode}/${desc}`
                        totalMCs += Number(object.moduleCredit);

                        return (
                            <tr key={moduleCode} >
                                <a href={link} 
                                        target="_blank" 
                                        rel="noopener noreferrer"
                                        className="text-white text-decoration-none">
                                            {render}
                                    </a>
                            </tr>)   
                    } 


            })
        )
    }

        return (
            <div>
                <h3>{props.title}</h3>
                <table className="table table-hover settings-table">
                    <tbody>
                        {makeTable()}
                    </tbody>
                </table>
            </div>
        )
}
