import React, { useState } from "react";
import AutoCompleteText from './AutocompleteText';
import { DropdownButton, Dropdown, Button } from 'react-bootstrap';
import ModuleCard from './Card';

function Board (props) {

    const [isTextBoxOpen, setOpen] = useState(false);
    const [moduleCodeTitle, setModuleCodeTitle] = useState([]);
    const drop = e => {
        e.preventDefault();
        const card_id = e.dataTransfer.getData('card_id');
        const card = document.getElementById(card_id);
        card.style.display = 'block';

        e.target.appendChild(card);
    }

    const dragOver = e => {
        e.preventDefault();
    }

    function handleButtonClick() {
        setOpen(!isTextBoxOpen);
     }

    function generateModuleCards () {


        return(
            moduleCodeTitle.map((item, i) => {
                return (
                    <ModuleCard
                        id={"card_" + i}
                        className="card"
                        draggable="true">
                        <p>{item}</p>
                    </ModuleCard>
                )
            })

        )

    }

    return (

        <div   
            id={props.id}
            onDrop={drop}
            onDragOver={dragOver}
        >
            <h3>{props.year}</h3>
            {generateModuleCards()}
            {isTextBoxOpen && <AutoCompleteText generateCards={this.updateModuleCards}/>}
            <Button onClick={handleButtonClick}>Add Module</Button>
        </div>
    )
}

export default Board;
