import {
    CALCULATE_CAP,
    SET_SEMESTER_OPTIONS,
    CLEAN_UP_CAP,
    SET_CAP
} from "./types";

export const calculateCAP = (selectedModules, category) => {
    return {
        type: CALCULATE_CAP,
        payload: selectedModules,
        category
    }
}

export const setCAP = (CAP) => {
    return {
        type: SET_CAP,
        payload: CAP
    }
}

export const setSemesterOptions = (years) => {
    return {
        type: SET_SEMESTER_OPTIONS,
        payload: years
    }
}

export const cleanUpCAP = () => {
    return { 
        type: CLEAN_UP_CAP
    }
}
