import React, { useState } from "react";
import Options from "./Options";
import isEmpty from "is-empty";
import { Card } from "react-bootstrap";
import PropTypes from "prop-types";
import { connect } from "react-redux";

const DegreeSettings = (props) => {
    const [degreeEditing, setDegreeEditing] = useState(false);
    const [addDegree, setAddDegree] = useState(false);
    return (
        <div>
        <Card className="container" id="degree-settings">
            <Card.Header className="card-header">
                {props.status === "first" 
                    ? "Degree Settings" 
                    : `${props.status} Degree Settings`}
            </Card.Header>

            <table className="table table-hover settings-table" id="degree-acad-table">
            <tbody>
                <Options
                    status={props.status}
                    editing={degreeEditing}
                    label={props.status === "first" ? "Faculty : " : `${props.status} Faculty : `}
                    handleChange={props.handleChange}
                    name={"faculty"}
                    value={props.userInput.faculty}
                    optionList={props.facultyOptions}/>

                <Options
                    status={props.status}
                    editing={degreeEditing}
                    label="Degree : "
                    handleChange={props.handleChange}
                    name="major"
                    value={props.userInput.major}
                    optionList={(props.userInput.faculty && !isEmpty(props.facultyOptions)) 
                        ? props.facultyOptions[props.userInput.facIndex].undergraduate.degrees
                            : null}/>

                <Options 
                    status={props.status}
                    editing={degreeEditing}
                    hidden={true}
                    title="Add Specialisation"
                    label="Specialisation : "
                    handleChange={props.handleChange}
                    name="specialisation"
                    value={props.userInput.specialisation}
                    optionList={(props.userInput.major && !isEmpty(props.facultyOptions))
                        ?  props.facultyOptions[props.userInput.facIndex].undergraduate.degrees
                        : null}/>

                <Options 
                    status={props.status}
                    editing={degreeEditing}
                    hidden={true}
                    label="Second Major : "
                    handleChange={props.handleChange}
                    name="secondMajor"
                    value={props.userInput.secondMajor}
                    optionList={(props.userInput.major && !isEmpty(props.facultyOptions)) 
                        ? props.facultyOptions[props.userInput.facIndex].undergraduate.secondMajors
                        : null}/>

                <Options 
                    status={props.status}
                    editing={degreeEditing}
                    hidden={true}
                    label="Minor : "
                    handleChange={props.handleChange}
                    name="minor"
                    value={props.userInput.minor}
                    optionList={(props.userInput.major && !isEmpty(props.facultyOptions)) 
                        ? props.facultyOptions[props.userInput.facIndex].undergraduate.minors
                        : null}/>

            </tbody>
        </table>

       {!degreeEditing 
            ? <button 
                className="button settings-button" 
                onClick={() => setDegreeEditing(true)}>
                    Edit Settings
            </button>
            : <button 
                className="button settings-button" 
                onClick={() => setDegreeEditing(false)}>
                    Save Settings
            </button>} 

        {!addDegree && (
            <button 
                className="button settings-button" 
                onClick={() => setAddDegree(true)}>
                Add Degree
            </button>
        )}

        {props.status !== "first" && (
            <button 
                className="button settings-button" 
                onClick={() => setAddDegree(!addDegree)}>
                Delete Degree
            </button>
        )}

        </Card>

        {addDegree && (
            <DegreeSettings
            status="Second"
            userInput={props.userInput}
            handleChange={props.handleChange}
            facultyOptions={props.facultyOptions}
            />
        )}
    </div>)
}


DegreeSettings.propType = {
    settings: PropTypes.object.isRequired
}


const mapStateToProps = state => ({
    settings: state.settings
})

export default connect(mapStateToProps) (DegreeSettings);
