import React from "react";

export class Dropdown extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            cat: this.props.cat,
            open: false,
            title: this.props.cat,
            prevMCs: 0,
            currentMCs: 0,
            getMCsNow: false
        }
        this.toggle = this.toggle.bind(this);
        this.getMCs=this.getMCs.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }

    static getDerivedStateFromProps(nextProps, prevState){
        if(nextProps.moduleCat === prevState.cat){ 
            if(nextProps.MCTemp === prevState.currentMCs) {       
          return {
                  title: nextProps.title,
                  getMCsNow: true
                  };
                }
            else {
                return {
                    title: nextProps.title,
                    prevMCs: prevState.currentMCs,
                    currentMCs: nextProps.MCTemp,
                    getMCsNow: true
                }
            }
        }
        else return null;
      }

    toggle() {
        this.setState({open: !this.state.open})
    }

    getMCs() {
        console.log('get MCs executed' + this.state.currentMCs);
         this.props.sendData(this.state.currentMCs);

    }

    handleClick() {
        this.setState({open: false});
        if(this.state.getMCsNow) {
            this.getMCs();
        }
    }

    render(){
        return(
        <div>
        <button type="button" 
                class="btn btn-dark" 
                data-toggle="tooltip" title="Click to expand list and select a module"
                onClick={() => this.toggle()}
                >
                    <h5>{this.state.title}</h5>
                    <h5>{this.state.currentMCs}</h5>
                    <h5>{this.state.prevMCs}</h5>
        </button>

            {this.state.open && (
                <table className="table table-dark table-hover">
                    <tbody onClick={() => this.handleClick()}>
                        {this.props.module}
                    </tbody>
                </table>)
                } 


        </div>

        )
    }
}
