import React , { useState } from 'react';
import { Card } from 'react-bootstrap';
import Board from './Board';

function YearDisplay(props) {
    const [mod, setMod] = useState(props.selectedModules);

    return (
        <div>
        <h2>{props.year}</h2>
        <h1>{mod}</h1>
        <Card style={{width: '360px'}}>
            <Card.Body>
                <div className="row">
                    <div className="col">
                        <Board 
                                updateSelectedModules={props.updateSelectedModules}
                                selectedModules={props.selectedModules}
                                id={props.year + " " + "Semester 1"}
                                className="board"
                                semester="Semester 1">

                        </Board>
                    </div>

                    <div className="col">
                        <Board 
                                updateSelectedModules={props.updateSelectedModules}
                                selectedModules={props.selectedModules}
                                id={props.year + " " + "Semester 2"}
                                className="board"
                                semester="Semester 2">

                        </Board>
                    </div>
                </div>
                <h5>Total MCs</h5>
            </Card.Body>
        </Card>
        </div>
    )
}

export default YearDisplay;
