import React from "react";

export class Dropdown extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            cat: this.props.cat,
            open: false,
            title: this.props.cat,
            prevMCs: 0,
            currentMCs: 0
        }
        this.toggle = this.toggle.bind(this);
    }

    static getDerivedStateFromProps(nextProps, prevState){
        if(nextProps.moduleCat === prevState.cat){ 
            if(nextProps.MCTemp === prevStateprevMCs)         
          return {
                  title: nextProps.title,
                  currentMCs: nextProps.MCtemp};
        }
        else return null;
      }

    toggle() {
        this.setState({open: !this.state.open})
    }

    getMCs() {
        if(this.state.MCs != prevState,MCs) {
        this.props.sendData(this.state.MCs)}
    }

    render(){
        return(
        <div>
        <button type="button" 
                class="btn btn-dark" 
                data-toggle="tooltip" title="Click to expand list and select a module"
                onClick={() => this.toggle()}
                >
                    <h5>{this.state.title}</h5>
        </button>

            {this.state.open && (
                <table className="table table-dark table-hover">
                    <tbody onClick={()=> this.setState({open:false})}>
                        {this.props.module}
                    </tbody>
                </table>)} 
            {this.getMCs()}
        </div>

        )
    }
}
