import React from "react";
import Options from "./Options";
import HiddenOptions from "./HiddenOptions";

const DegreeSettings = (props) => {
    return (
        <div>
            {!isEmpty(props.settings.facultyOptions) && (
                <Options
                label="Your Faculty: "
                handleChange={props.handleChange}
                name="faculty"
                value={userInput.faculty}
                optionList={props.settings.facultyOptions}/>)}

            {userInput.faculty && !isEmpty(props.settings.facultyOptions) && (
                <div>
                    <Options
                        label="Your Degree: "
                        handleChange={props.handleChange}
                        name="major"
                        value={userInput.major}
                        optionList={props.settings.facultyOptions[userInput.facIndex].undergraduate.degrees}/>

                    <HiddenOptions 
                        title="Add Specialisation"
                        label="Your Specialisation: "
                        handleChange={props.handleChange}
                        name="specialisation"
                        value={userInput.specialisation}
                        optionList={userInput.major && props.settings.facultyOptions[userInput.facIndex].undergraduate.degrees}/>

                    <HiddenOptions 
                        title="Add Second Major"
                        label="Your Second Major: "
                        handleChange={props.handleChange}
                        name="secondMajor"
                        value={userInput.secondMajor}
                        optionList={props.settings.facultyOptions[userInput.facIndex].undergraduate.secondMajors}/>

                    <HiddenOptions 
                        title="Add Minor"
                        label="Your Minor: "
                        handleChange={props.handleChange}
                        name="minor"
                        value={userInput.minor}
                        optionList={props.settings.facultyOptions[userInput.facIndex].undergraduate.minors}/>
                </div>)}
        </div>
    )
}

export default DegreeSettings;
