import { Form, Button, Select } from 'antd';
import { MinusCircleOutlined, PlusOutlined } from '@ant-design/icons';
import { generateOptions } from "../utils/commonFunctions";
// import AutocompleteText from "../Pages/Module Planner Page/AutocompleteText";
import React, { useState, useEffect} from "react";
import isEmpty from 'is-empty';

const { Option } = Select;

// const formItemLayout = {
//     labelCol: {
//       xs: { span: 24 },
//       sm: { span: 4 },
//     },
//     wrapperCol: {
//       xs: { span: 24 },
//       sm: { span: 20 },
//     },
//   };
  // const formItemLayoutWithOutLabel = {
  //   wrapperCol: {
  //     xs: { span: 24, offset: 0 },
  //     sm: { span: 20, offset: 4 },
  //   },
  // };

  const DynamicFieldSet = (props) => {
    const [options, setOptions] = useState(props.optionList);
    const [keyList, setKeyList] = useState([0]);

    const renderIntial = () => {
        return keyList.map((key) => {
          return (
            <div key={key}>
              <Select
                showSearch
                onChange={(e, object) => onChange(e, object, index)}
                defaultValue="None "
                value={obj ? obj.name : "None "}
                style={{ width: 250 }}
                optionFilterProp="children"
                filterOption={(input, option) => {
                    return option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                }}>
                <Option 
                    key={"choose" + props.label}
                    value="None " 
                      disabled>
                    {"Choose " + props.label}
                </Option>
                {generateOptions((options ? options : props.optionList), props.name)}
              </Select>

              {key >= 1 ? (
                <MinusCircleOutlined
                  className="dynamic-delete-button"

                  onClick={() => {
                    handleRemove(key);
                  }}
                />
              ) : null}
            </div>
          )
        })
  }

    const handleRemove = (index) => {
      const updated = [...props.value];
      if(updated[index]) {
         //add removed option to options in state
      const temp = [...options];
      for(let i = 0; i < props.optionList.length; i++) {
        if(props.optionList[i].fullName === updated[index].name) {
          temp.splice(i, 1, props.optionList[i]);
          break;
        }
      }
      setOptions(temp);

      //remove option from userInput state in acad settings page
      updated.splice(index, 1);
      props.setUserInput({[props.name]: updated});
      }
    }

    const onChange = (e, object, index) => {
      const updated = [...props.value];

      const temp = [...options];
        for(let i = 0; i < props.optionList.length; i++) {
          if(updated[index]) {
             //add removed option back into options
            if(props.optionList[i].fullName === updated[index].name) {
              temp.splice(i, 1, props.optionList[i]);
            }
          }

          if(props.optionList[i].fullName === object.value) {
            temp.splice(i, 1, null);
          } 
        }
       setOptions(temp);

      updated[index] = {
                        name: object.value,
                        tag: object.tag
                      };
      props.setUserInput({[object.name]: updated});
  }

  const checkIsOptionsEmpty = () => {
    for(let i = 0; i < options.length; i++ ) {
      if(options[i]) {
        return false;
      } 
    }
    return true;
  }



    return (
      <Form 
        name="dynamic_form_item" 
        // form={form}
        // fields={fields}
        {...formItemLayoutWithOutLabel}>

        <Form.List 
          name={props.name}>
          {(fields, { add, remove }) => {
            console.log(fields)
            return (
              <div>
                {renderIntial()}
                {fields.map((field, index) => (

                  <Form.Item
                    {...(formItemLayoutWithOutLabel)}
                    required={false}
                    key={field.key}
                  >
                    <Form.Item
                      // {...field}
                      // validateTrigger={['onChange', 'onBlur']}
                      // rules={[
                      //   {
                      //     required: true,
                      //     whitespace: true,
                      //     message: `Please input ${props.name} or delete this field.`,
                      //   },
                      // ]}
                      noStyle
                    >
                      <Select
                        showSearch
                        onChange={(e, object) => onChange(e, object, (index + props.value.length))}
                        defaultValue="None "
                        value={props.value[index + props.value.length] ? props.value[index + props.value.length].name : "None "}
                        style={{ width: 250 }}
                        optionFilterProp="children"
                        filterOption={(input, option) => {
                            return option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }}>
                        <Option 
                            key={"choose" + props.label}
                            value="None " 
                             disabled>
                            {"Choose " + props.label}
                        </Option>
                        {generateOptions((options ? options : props.optionList), props.name)}
                      </Select>
                    </Form.Item>

                    {fields.length > 1 ? (
                      <MinusCircleOutlined
                        className="dynamic-delete-button"

                        onClick={() => {
                          remove(field.name);
                          handleRemove(index + props.value.length);
                        }}
                      />
                    ) : null}
                  </Form.Item>
                ))}


                {/* {!checkIsOptionsEmpty() &&  */}
                <Form.Item>
                  <Button
                      type="dashed"
                      onClick={() =>
                          // if(props.value[index] || (index + 1)=== 0) {
                            add()
                          // }
                        }
                          rules={[
                              {
                                required: true,
                                whitespace: true,
                                message: `Please input ${props.name} or delete this field.`,
                              }]
                      }
                      style={{ width: '100%' }}>
                       <PlusOutlined /> Add {props.name === "secondMajors" ? "Major" : props.name}
                    </Button>
                  </Form.Item>
                  {/* } */}
              </div>
            );
          }}
        </Form.List>

        {/* <Form.Item>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item> */}
      </Form>
    );
  };


export default DynamicFieldSet;
