const checkSubmission = (userData) => {
  const keys = Object.keys(userData);
  let status;

  for (let i = 0; i < keys.length; i++) {
    const key = keys[i];
    status = (userData[key] || userData[key] === 0) ? true : false;
    console.log(status);
    console.log(userData[key]);
    if(!status) {
      return status = false;
