import React from 'react';
import "./AutocompleteSearch.css";

class AutoCompleteText extends React.Component {
    constructor (props) {
        super(props);
        this.items = ['Fris','Dani','Hey','Ho'];
        this.state = { suggestions: [],
                       text: ''
                     };
        this.handleTextChange = this.handleTextChange.bind(this);
        this.renderSuggestions = this.renderSuggestions.bind(this);
        this.suggestionsSelected = this.suggestionsSelected.bind(this);
    }

    componentDidMount() {
        // Call our fetch function below once the component mounts
      this.callBackendAPI()
        .then(res => this.setState({ module: res }))
        .catch(err => console.log(err));
    }
      // Fetches our GET route from the Express server. (Note the route we are fetching matches the GET route from server.js
    callBackendAPI = async () => {
      const response = await fetch('https://api.nusmods.com/v2/2019-2020/moduleInfo.json', {'accept': 'application/json'});
      const body = await response.json();

      if (response.status !== 200) {
        throw Error(body.message) 
      }
      console.log(body);
      return body;
    };

    parseData () {
        this.state.module.map((object) => {
            const { moduleCode, title, moduleCredit} = object;
            this.setState({ moduleCode: moduleCode,
                            moduleTitle: title,
                            moduleCredit: moduleCredit});
        })
    }

    handleTextChange (e) {
        const value = e.target.value;
        let suggestions = [];
        if (value.length > 0) {
            const regex = new RegExp(`^${value}`, 'i');
            suggestions = this.state.res.moduleCode.sort().filter(v => regex.test(v));
        }
        this.setState(() => ({ suggestions, text: value }));
    }

    renderSuggestions () {
        const { suggestions } = this.state;
        if (suggestions.length === 0) {
            return null;
        }

        return (
            <ul>
                {suggestions.map((item) => <li onClick={() => this.suggestionsSelected(item)}>{item}</li>)}
            </ul>
        );

    }

    suggestionsSelected (value) {
        this.setState(() => ({
            text: value,
            suggestions: [],
        }))
    }

    render () {
        const { text } = this.state;
        return (
                <div className="AutoCompleteText">
                <input 
                       value={text}
                       onChange={this.handleTextChange}
                       type="text" />
                {this.renderSuggestions()}
                </div>
        )
    }
}

export default AutoCompleteText;
