import React from 'react';
import Board from './Board';
import Rules from './Rules';
import './plannertemp.css';
import { Button, Card } from 'react-bootstrap';


class ModulePlannerPageTemp extends React.Component {
   constructor(props) {
       super(props);
       this.state = {   
                        modulesSelected: null,
                        rules: [{_id:"5ed0d9a9a9225a12882ec26d",
                                name:"Degree Requirement",
                                desc:"Complete at least 20MCs worth of General Education Modules, with at least 1 module in each of the GEH, GEQ, GER, GES and GET pillars",
                                tag:"r_ulr",
                                sub:[
                                        {_id:"5ed0dd49a9225a12882ec26e",
                                        name:"University Level Requirement",
                                        desc:"Complete a GEH-coded module",
                                        tag:"r_geh_pillar",
                                        sub: [
                                                {_id:"5ed0dd54a9225a12882ec26f",
                                                name:"GEQ Pillar",
                                                desc:"Complete a GEQ-coded module",
                                                tag:"r_geq_pillar"},

                                                {_id:"5ed0dd60a9225a12882ec270",
                                                name:"GER Pillar",
                                                desc:"Complete a GER-coded module",
                                                tag:"r_ger_pillar"},

                                                {_id:"5ed0dd6ca9225a12882ec271",
                                                name:"GES Pillar",
                                                desc:"Complete a GES-coded module",
                                                tag:"r_ges_pillar"},

                                                {_id:"5ed0dd76a9225a12882ec272",
                                                name:"GET Pillar",
                                                desc:"Complete a GET-coded module",
                                                tag:"r_get_pillar"}
                                            ]},

                                        {_id:"5ed0dd54a9225a12882ec26f",
                                        name:"GEQ Pillar",
                                        desc:"Complete a GEQ-coded module",
                                        tag:"r_geq_pillar"},

                                        {_id:"5ed0dd60a9225a12882ec270",
                                        name:"GER Pillar",
                                        desc:"Complete a GER-coded module",
                                        tag:"r_ger_pillar"},

                                        {_id:"5ed0dd6ca9225a12882ec271",
                                        name:"GES Pillar",
                                        desc:"Complete a GES-coded module",
                                        tag:"r_ges_pillar"},

                                        {_id:"5ed0dd76a9225a12882ec272",
                                        name:"GET Pillar",
                                        desc:"Complete a GET-coded module",
                                        tag:"r_get_pillar"}
                                    ]

                                }],
        }

    }

    async callBackendFunc(ruleTag) {
        const link = "http://localhost:5000/rules/" + ruleTag;
        const requestOptions = {
            method: 'POST',
            mode: 'no-cors',
            headers: { 'Content-Type': 'application/json',
                        'accept': 'application/json'            
            },
            body: JSON.stringify({modules: this.state.modulesSelected})
        };
        this.setState({isLoading: true});

        await fetch(link, requestOptions)
            .then(async response => {
                const status = await response.json();

                if(!response.ok) {
                    throw new Error("An error has occurred")
                }

                return status;
            })
            .then (status => {
                this.setState({status: status,
                                isLoading: false})
            })
            .catch(error => {
                      alert('nooope');
                      this.setState({ errorMessage: error.toString()});
                      console.error('There was an error!', error);
                  });

    }

    render () {
        return (
            <div className="container">
                <Board 
                        id="board-1"
                        className="board"
                        year="Year 1"
                        generateModuleCards={this.generateModuleCards}>

                </Board>

                <Board 
                        id="board-1"
                        className="board"
                        year="Year 2"
                        generateModuleCards={this.generateModuleCards}>

                </Board>

                <Board 
                        id="board-1"
                        className="board"
                        year="Year 3"
                        generateModuleCards={this.generateModuleCards}>

                </Board>

                <Board 
                        id="board-1"
                        className="board"
                        year="Year 4"
                        generateModuleCards={this.generateModuleCards}>

                </Board>
                <br/>

                <Button onClick={() => {this.setState({callBackendNow: true})}}>Evaluate</Button>
                <br/>
                <br/>

                <Card>
                    <Rules
                        rules={this.state.rules}
                        callBackendNow={this.state.callBackendNow}
                        callBackendFunc={this.callBackendFunc}/>
                </Card>
                <br/>

            </div>
        )
    }
}

export default ModulePlannerPageTemp;
