import {
    SET_SEMESTER_OPTIONS,
    CALCULATE_CAP,
    CLEAN_UP_CAP,
} from "../actions/types";

const initialState = {
    transcript: [],
    targetGrade: [],
    cap: null,
    semesterOptions: []
};

export default function(state = initialState, action) {
    switch (action.type) {
        case SET_SEMESTER_OPTIONS: 
            let sem1;
            let sem2;
            let semesterOptions = [];

            for(let i = 1; i < (action.payload * 2); i += 2) {
                const year = Math.ceil(i / 2);
                sem1 = `Year ${year} Semester 1`;
                sem2 = `Year ${year} Semester 2`;
                semesterOptions[i - 1] = sem1;
                semesterOptions[i] = sem2;
            }

            return {
                ...state,
                semesterOptions: semesterOptions
            }

        case CALCULATE_CAP:
            let totalMC = 0;
            let totalGradePoint = 0;
            for(let i = 0; i < action.payload.length; i ++) {
                const module = action.payload[i];
                if(module.grade && !module.SU) {
                    totalGradePoint += (module.gradePoint * module.moduleCredit);
                    totalMC += module.moduleCredit;
                }
            }

            const cap = totalGradePoint / totalMC;
            return {
                ...state,
                cap: cap
            }

        case CLEAN_UP_CAP:
            return initialState;

        default: 
            return state;
    }
}
