import React, { useState } from "react";
import AutoCompleteText from "../Pages/Module Planner Page/AutocompleteText";
import { generateOptions } from "../utils/commonFunctions";
import isEmpty from "is-empty";
import { Switch, Select } from "antd";
import DynamicFieldSet from "./DynamicFieldSet";

const Options = (props) => {
    const [isOpen, setIsOpen] = useState(props.value ? true : false);
    const { Option } = Select;
    let value;
    if (props.value) {
        value = props.value.name ? props.value.name : props.value;
    } else {
        value = "None";
    } 

    const renderContent = () => {
        if(props.editing) {
            if(!props.hidden) {
                return (
                    <Select
                        showSearch
                        onChange={props.handleChange}
                        defaultValue="None "
                        value={value}
                        style={{ width: 300 }}
                        optionFilterProp="children"
                        filterOption={(input, option) => {
                            return option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }}>
                        <Option 
                            key={"choose" + props.label}
                            value="None " 
                             disabled>
                            {"Choose " + props.label}
                        </Option>
                        {generateOptions(props.optionList, props.name)}
                    </Select> ) 
            } else if (isOpen) {
                return (
                    <div>
                    <DynamicFieldSet
                        label={props.label}
                        handleChange={props.handleChange}


                        setUserInput={props.setUserInput}
                        options={props.optionList}
                        name={props.name}
                        value={props.value}/>
                    </div>
                )
            } else {
                return value;
            }
        } else {
            return value;
        }
    }
    return (
            <tr>
                <td>
                    <label>{props.label}</label>
                </td>

                <td>
                    {renderContent()}
                </td>

                <td>
                    {props.hidden && props.editing
                        ? <Switch  
                            checked={isOpen} 
                            onChange={() => setIsOpen(!isOpen)}/>
                        : null}
                </td>
            </tr>
    )        
}

export default Options;
