import {
    SET_BOARD_FILLED, SET_MODULES, SET_RULES 
} from "../actions/types";

const initialState = {
    isBoardFilled: false,
    isTextBoxOpen: false,
    selectedModules: [], 
    callBackendNow: false,
    rules: []
}

export default function (state = initialState, action) {
    switch (action.type) {
        case SET_BOARD_FILLED: 
            return {
                ...state,
                selectedModules: true
            }
        case SET_MODULES: 
            return {
                ...state,
                modules: action.payload
        }
        case SET_RULES: 
            return {
                ...state,
                rules: action.payload
        }
        default:
            return state;
    }
}
