import React from 'react';
import Subrules from './Subrules';

class Rules extends React.Component {
    constructor(props) {
        super(props);
        this.state = {isOpen: false}
        this.generateRules = this.generateRules.bind(this);
    }

    generateRules() {
        const rule = this.props.rules;
        // if(!Array.isArray(rule)) {
        //     rule = [rule];   
        // } 
        // console.log(rule)
        return (
            <div>
                {   
                    rule.map(rule => (
                                        <ul>
                                            <Subrules
                                                onClick={() => this.setState({isOpen: !isOpen})}
                                                ruleName={rule.name}
                                                ruleTag={rule.tag}
                                                ruleDesc= {rule.desc}
                                                callBackendNow={this.props.callBackendNow}
                                                selectedModules={this.props.selectedModules}
                                                updateCallBackendNow={this.props.updateCallBackendNow}/>

                                        {rule.tag && rule.params.list 
                                            && rule.params.list[0] && rule.params.list[0].name && isOpen(
                                                <Rules
                                                    rules={rule.params.list}
                                                    callBackendNow={this.props.callBackendNow}
                                                    selectedModules={this.props.selectedModules}
                                                    updateCallBackendNow={this.props.updateCallBackendNow}/>
                                            )
                                        }
                                        </ul>
                                    )
                            )
                }
            </div>
        )
    }

    render() {
        return (
            <div>
                {this.generateRules()}
            </div>
        )
    }
}

export default Rules;
