import React from 'react';
import Board from './Board';
import ModuleCard from './Card';
import './plannertemp.css';
import AutoCompleteText from './AutocompleteText';
import { DropdownButton, Dropdown, Button } from 'react-bootstrap';

let newModuleCodeTitle = [];

class ModulePlannerPageTemp extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
                        isTextBoxOpen: false,
                        moduleCodeTitle: []
                     };
        this.generateModuleCards = this.generateModuleCards.bind(this);
        this.handleButtonClick = this.handleButtonClick.bind(this);
        this.updateModuleCards = this.updateModuleCards.bind(this);
    }

    updateModuleCards(item) {
        if (!newModuleCodeTitle.includes(item)) {
            newModuleCodeTitle.push(item);
            this.setState({moduleCodeTitle: newModuleCodeTitle});
        }
    }

    generateModuleCards () {
        const moduleCodeTitle = this.state.moduleCodeTitle;

        return(
            moduleCodeTitle.map((item, i) => {
                return (
                    <ModuleCard
                        id={"card_" + i}
                        className="card"
                        draggable="true">
                        <p>{item}</p>
                    </ModuleCard>
                )
            })

        )

    }

    handleButtonClick() {
       this.setState({isTextBoxOpen: !this.state.isTextBoxOpen})
       this.generateModuleCards();
    }

    render () {
        return (
            <div className="container">


                <Board 
                        id="board-1"
                        className="board">
                    <h3>Year 1</h3>
                    {this.generateModuleCards()}
                    {this.state.isTextBoxOpen && <AutoCompleteText generateCards={this.updateModuleCards}/>}
                    <Button onClick={this.handleButtonClick}>Add Module</Button>

                </Board>

                <Board 
                        id="board-1"
                        className="board">
                    <h3>Year 2</h3>
                    {this.generateModuleCards()}
                    {this.state.isTextBoxOpen && <AutoCompleteText generateCards={this.updateModuleCards}/>}
                    <Button onClick={this.handleButtonClick}>Add Module</Button>
                </Board>

                <Board 
                        id="board-1"
                        className="board">
                    <h3>Year 3</h3>
                    {this.generateModuleCards()}
                    {this.state.isTextBoxOpen && <AutoCompleteText generateCards={this.updateModuleCards}/>}
                    <Button onClick={this.handleButtonClick}>Add Module</Button>
                </Board>

                <Board 
                        id="board-1"
                        className="board">
                    <h3>Year 4</h3>
                    {this.generateModuleCards()}
                    <Button onClick={this.handleButtonClick}>Add Module</Button>
                </Board>

            </div>
        )
    }
}

export default ModulePlannerPageTemp;
