import React, { useState, useEffect } from "react";
import { Card, Spinner } from "react-bootstrap";

function Subrules(props) {
    const [isLoading, setIsLoading] = useState(false);
    const [isRuleFulfilled, setIsRuleFulfilled] = useState(false);

    useEffect(() => {
        const callBackendFunc = async (ruleTag) => {
            const link = "http://172.31.21.121:3000/rules/" + ruleTag;
            const requestOptions = {
                method: 'POST',
                mode: 'no-cors',
                headers: { 'Content-Type': 'application/json',
                            'accept': 'application/json'            
                },
                body: JSON.stringify({modules: props.selectedModules})
            };
            setIsLoading(true);

            await fetch("http://172.31.21.121:3000/rules/r_cs_foundation", requestOptions)
                .then(async response => {
                    const status = await response.json();

                    if(!response.ok) {
                        throw new Error("An error has occurred")
                    }

                    return status;
                })
                .then (status => {
                    setIsRuleFulfilled(status);
                    setIsLoading(false);
                    alert('success');
                })
                .catch(error => {
                          setIsLoading(false);
                          console.error('There was an error!', error);
                      });

        }
        callBackendFunc(props.ruleTag);
    }, [props.callBackendNow]);



    return (    
        <div>
            <Card.Title>{props.ruleName}</Card.Title>
            {isLoading && <Spinner animation="border" variant="success" role="status" as="span">
                <span className="sr-only">Loading...</span>
            </Spinner>}
        </div>
    )

}

export default Subrules;
