        {...formItemLayoutWithOutLabel}>

        <Form.List 
          name={props.name}>
          {(fields, { add, remove }) => {
            console.log(fields)
            return (
              <div>
                {/* {Array.isArray(fields) ? this.addMultipleFields() : this.addSingleField()} */}
                {fields.map((field, index) => (

