import React from "react";
import { Card, Spinner } from "react-bootstrap";

class Subrule extends React.Component {
    constructor(props) {
        super(props);
        this.state = {  isLoading: false,

                     }

    }



    render() {
        return (    <div>
                        <Card.Title>{this.props.ruleName}</Card.Title>
                        {this.state.isLoading && <Spinner animation="border" variant="success" role="status" as="span">
                            <span className="sr-only">Loading...</span>
                        </Spinner>}
                        {this.props.callBackendNow && this.props.callBackendFunc(this.props.ruleTage)}
                    </div>
                )

    }
}

export default Subrule;
