import React from "react";

//list of faculties
const faculties = ['Arts and Social Sciences', 'Business', 
                  'Computing', 'Dentistry',
                  'Design & Environment', 'Engineering',
                  'Law', 'Medicine', 'Music','Science' ];

const faculty_options = faculties.map((faculties) => 
  (<option value={faculties} >
    {faculties}
    </option>)
  );

//list of majors according to faculty

export class Input extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    const name = e.target.value;
    this.props.onChange(name);
  }

  render() {
       return (
       <div>
       <label>Your Faculty  </label>
       <select
          id="Faculty">
           {faculty_options}
        </select>   
        <br/>
        <br/>
        <label>Your Major  </label>
        <select
          id="Faculty">
           {faculty_options}
        </select>
        <br/>
        <br/>
        <label>Your Specialisation  </label>
        <select
          id="Faculty">
           {faculty_options}
        </select>
        <br/>
        <br/>
        <label>Your Residence  </label>
        <select
          id="Faculty">
           {faculty_options}
        </select>

      </div>
      );
    }
}


export default Input;
