import React from "react";
import { generateOptions } from "../../utils/commonFunctions";
import { Select } from "antd";

export const Table = (props) => {
    let totalMCs = 0;

    const makeTable = () => {
        return (
            props.module.filter((object, i) => object.location === props.title)
            .map((object, i) => {
                    const {moduleCode, title, moduleCredit} = object;
                    const render = (
                        <div>
                        <td>{moduleCode}</td>
                        <td>{title}</td>
                        <td>{moduleCredit + 'MCs'}</td>
                        </div>);

                    if(props.category === "tables") {
                        const desc = title.replace(/ /g, '-');
                        const link = `https://nusmods.com/modules/${moduleCode}/${desc}`
                        totalMCs += Number(object.moduleCredit);

                        return (
                            <tr key={moduleCode} >
                                <a href={link} 
                                        target="_blank" 
                                        rel="noopener noreferrer"
                                        className="text-white text-decoration-none">
                                            {render}
                                    </a>
                            </tr>
                        )   
                    } else {
                        return (
                            <tr key={moduleCode}>
                                {render}
                                <td>
                                    <Select 
                                        name={object}
                                        value={object.grade || object.targetGrade} 
                                        onChange={props.handleGradeClick}
                                        style={{ width: 70 }}>
                                        {generateOptions(props.gradeList, "grade", object)}
                                    </Select>
                                </td>
                                {props.isPast && 
                                <td>
                                    <input
                                        className="checkbox"
                                        type="checkbox"
                                        checked={object.SU}
                                        onChange={(e) => props.handleCheckboxChange(e, object)}/>
                                </td>}
                                <td>
                                    <i  
                                        className="fa fa-trash-alt fa-border"
                                        style={{cursor: "pointer"}}
                                        onClick={() => props.setModuleLocation({id: object.moduleCode}, null, null, props.modplan.selectedModules)} />
                                </td>
                            </tr>
                        )
                    }


            })
        )
    }

        return (
                props.category 
                    ? (
                        <div>
                            <h3>{props.title}</h3> 
                            <table className="table table-hover settings-table">
                                <tbody>
                                    {makeTable()}
                                </tbody>
                            </table>
                        </div>
                    )
                    : (
                        <div>
                            <h3>Courses taken this semester</h3>
                            <table className="table table-hover">
                                <th>Module Code</th>
                                <th>Module Title</th>
                                <th>Modular Credits</th>
                                <th>{props.isPast ? "Grade" : "Target Grade"}</th>
                                {props.isPast && <th>S/U</th>}
                                <th></th>
                                <tbody>
                                    {makeTable()}
                                </tbody>
                            </table>
                        </div>
                    )
        )
}
