        // if(!isEmpty(transcript)) {
        //     const { unique, indexOfDuplicate, newModule} = checkDuplicate(object, transcript);
        //     if(!unique) {
        //         newModule.grade = e.target.value;
        //         module = newModule;
        //     }
        //     isUnique = unique;
        //     index = indexOfDuplicate;
        // } 

