import {
    SET_USER_SETTINGS,
    SET_CURRENT_SEMESTER
} from "../actions/types";

const initialState = {
    // name: null,
    // password: null,
    // faculty: null,
    // major: null,
    // specialisation: null,
    // residential: null,
    // matriculationYear: null,
    // targetGraduationYear: null,
    // modPlan: []
    userInfo: null
}

export default function (state = initialState, action ) {
    switch(action.type) {
        case SET_USER_SETTINGS:
            return {
                ...state,
                userInfo: action.payload
            }

        case SET_CURRENT_SEMESTER:
            return {
                ...state,
                currentAY: action.currentAY,
                currentSemester: action.currentSemester
            }
        default:
            return state;
    }
}
