import React, {useState} from "react";

function Board (props) {

    const [isTextBoxOpen, setOpen] = useState(false);
    const drop = e => {
        e.preventDefault();
        const card_id = e.dataTransfer.getData('card_id');
        const card = document.getElementById(card_id);
        card.style.display = 'block';

        e.target.appendChild(card);
    }

    const dragOver = e => {
        e.preventDefault();
    }

    return (

        <div   
            id={props.id}
            onDrop={drop}
            onDragOver={dragOver}
        >
            <h3>{props.year}</h3>
            {props.generateModuleCards}
            {this.state.isTextBoxOpen && <AutoCompleteText generateCards={this.updateModuleCards}/>}
            <Button onClick={this.handleButtonClick}>Add Module</Button>
        </div>
    )
}

export default Board;
